<template>
	<view class="_ui-empty" :style="{paddingTop:`${this.offsetTop}rpx`,paddingBottom:`${this.offsetBottom}rpx`}">
		<image class="_ui-empty__png" @error="onErrorImage" :src="isCustom?'/static/empty/data.png':base64" />
		<text class="_ui-empty__description">{{description}}</text>
		<slot />
	</view>
</template>

<script lang="uts">
	/**
	 * ui-empty-data 暂无数据
	 * @description 《暂无数据》，根据异常的场景给予相关元素的缺省页，在产品页面相对应的场景特点来设计页面内容。
	 * @tutorial https://www.flowerui.com/documents/flower-empty/module/data.html
	 * @property {String} description 缺省页描述文字
	 * @property {Number} offsetTop 偏移向上距离
	 * @property {Number} offsetBottom 偏移向下距离
	 */
	export default {
		data() {
			return {
				base64: "data:image/png;base64,",
				isCustom: true
			}
		},
		props: {
			description: {
				type: String,
				default: "暂无数据"
			},
			offsetTop: {
				type: Number,
				default: 0
			},
			offsetBottom: {
				type: Number,
				default: 0
			}
		},
		methods: {
			onErrorImage() {
				this.isCustom = false;
			}
		}
	}
</script>

