<template>
	<view class="_ui-empty" :style="emptyStyle">
		<image class="_ui-empty__png" @error="onError" :src="isCustom?'/static/empty/permission.png':base64" />
		<text class="_ui-empty__description">{{description}}</text>
		<slot />
	</view>
</template>

<script lang="uts">
	/**
	 * ui-empty-permission 暂无权限
	 * @description 《暂无权限》，根据异常的场景给予相关元素的缺省页，在产品页面相对应的场景特点来设计页面内容。
	 * @tutorial https://www.flowerui.com/documents/flower-empty/module/permission.html
	 * @property {String} description 缺省页描述文字
	 * @property {Number} offsetTop 偏移向上距离
	 * @property {Number} offsetBottom 偏移向下距离
	 * @property {Boolean} isCustom 是否自定义缺省页图
	 */
	export default {
		data() {
			return {
				base64: "data:image/png;base64,"
			}
		},
		props: {
			description: {
				type: String,
				default: "暂无权限"
			},
			offsetTop: {
				type: Number,
				default: 0
			},
			offsetBottom: {
				type: Number,
				default: 0
			},
			isCustom: {
				type: Boolean,
				default: false
			}
		},
		computed: {
			emptyStyle() : object {
				return {
					paddingTop: `${this.offsetTop}rpx`,
					paddingBottom: `${this.offsetBottom}rpx`
				}
			}
		},
		methods: {
			onError: function () : void {
				console.error(`当前自定义 “ ${this.description} ” 缺省页配置不正确，请根据文档正确配置后使用。`)
			}
		}
	}
</script>

<style scoped>
	._ui-empty {
		/* #ifndef APP-PLUS-NVUE || UNI-APP-X */
		display: flex;
		flex-direction: column;
		/* #endif */
		align-items: center;
	}

	._ui-empty__png {
		height: 500rpx;
		width: 500rpx;
	}

	._ui-empty__description {
		font-size: 28rpx;
		width: 456rpx;
		margin-bottom: 24rpx;
		text-align: center;
		color: #A2ABB9;
	}
</style>